import Layout from '../components/MyLayout';
import Link from 'next/link';

function getPosts() {
    return [
        { id: 'hello-nextjs', title: 'Hello Next.js'},
        { id: 'learn-nextjs', title: 'Learn Nextjs is awesome'},
        { id: 'deploy-nextjs', title: 'Deploy apps nextjs'}
    ];
}

const PostLink = ({ post }) => (
    <li>
        <Link href="/p/[id]" as={`/p/${post.id}`}>
            <a>{post.title}</a>
        </Link>
    </li>
);


export default function Blog() {
    return (
        <Layout>
            <h1>My Blog</h1>
            <ul>
                {getPosts().map(post => (
                    <PostLink key={post.id} post={post} />
                ))}
            </ul>

            <style jsx>{`
                h1,
                a {
                    font-family: 'Arial';
                }
                ul {
                    padding: 0;
                }
                li {
                    list-style: none;
                    margin: 5px 0;
                }
                a {
                    text-decoration: none;
                    color: blue;
                }
                a:hover {
                    opacity: 0.6;
                }
            `}</style>
        </Layout>
    )
}
